    private void tbl_MostrarClienteMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tbl_MostrarClienteMouseClicked
        // TODO add your handling code here:
        int r = tbl_MostrarCliente.getSelectedRow();
        int id = (int) tbl_MostrarCliente.getValueAt(r, 0);
        String nombre = (String) tbl_MostrarCliente.getValueAt(r, 1);
        String apellidos = (String) tbl_MostrarCliente.getValueAt(r, 2);
        String direccion = (String) tbl_MostrarCliente.getValueAt(r, 3);
        String telefono = (String) tbl_MostrarCliente.getValueAt(r, 4);
        String mail = (String) tbl_MostrarCliente.getValueAt(r, 5);

        tf_Idcliente.setText("" + id);
        tf_nombre.setText(nombre);
        tf_apellidos.setText(apellidos);
        tf_domicilio.setText(direccion);
        tf_telefono.setText(telefono);
        tf_mail.setText(mail);

    }//GEN-LAST:event_tbl_MostrarClienteMouseClicked

    private void btn_BorrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_BorrarActionPerformed
        // TODO add your handling code here:
        ClienteDTO cliente = new ClienteDTO();
        cliente.setIdcliente(Integer.parseInt(tf_Idcliente.getText()));

        int resp = JOptionPane.showConfirmDialog(null, "¬ørealmente desea eliminar?", "confirmar ELIMINACION", JOptionPane.YES_NO_OPTION,
            JOptionPane.QUESTION_MESSAGE);
        if (resp == 0) {
            Conector cone = new Conector();
            ClienteDAO clientesDAO = new ClienteDAO(cone.miconector);
            int resultado = clientesDAO.borrarCliente(cliente);
            if (resultado > 0) {
                JOptionPane.showMessageDialog(null, "eliminacion erronia");
                refrescar();
            } else {
                JOptionPane.showMessageDialog(null, "eliminacion exitosa");
                refrescar();
                cone.cerrar();
            }
        }
    }//GEN-LAST:event_btn_BorrarActionPerformed

