    private void btn_actualiazarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_actualiazarActionPerformed
        // TODO add your handling code here:
        ClienteDTO cliente = new ClienteDTO();
        cliente.setIdcliente(Integer.parseInt(tf_Idcliente.getText()));
        cliente.setNombre(tf_nombre.getText());
        cliente.setApellidos(tf_apellidos.getText());
        cliente.setDomicilio(tf_domicilio.getText());
        cliente.setTelefono(tf_telefono.getText());
        cliente.setMail(tf_mail.getText());

        int resp = JOptionPane.showConfirmDialog(null, "¬ørealmente desea modificar?", "confirmar actualizacion", JOptionPane.YES_NO_OPTION,
            JOptionPane.QUESTION_MESSAGE);
        if (resp == 0) {
            Conector cone = new Conector();
            ClienteDAO clientesDAO = new ClienteDAO(cone.miconector);
            int resultado = clientesDAO.actulizarcliente(cliente);
            if (resultado > 0) {
                JOptionPane.showMessageDialog(null, "actualizacion exitosa");
                refrescar();
            } else {
                JOptionPane.showMessageDialog(null, "actualizacion erronia");
                cone.cerrar();
            }
        }
    }//GEN-LAST:event_btn_actualiazarActionPerformed

