     private void refrescar() {
        Conector cone = new Conector();

        ClienteDAO clienteDAO = new ClienteDAO(cone.miconector);
        List<ClienteDTO> cliente = clienteDAO.clientegeneralDAO();

        DefaultTableModel modeloTabla = new DefaultTableModel();
        tbl_MostrarCliente.setModel(modeloTabla);

        modeloTabla.addColumn(" idcliente");
        modeloTabla.addColumn(" nombre");
        modeloTabla.addColumn(" apellidos");
        modeloTabla.addColumn(" domicilio");
        modeloTabla.addColumn(" telefono");
        modeloTabla.addColumn(" mail");

        Object[] columna = new Object[5];

        int objGuardados = cliente.size();

        for (int i = 0; i < objGuardados; i++) {
            columna[0] = cliente.get(i).getIdcliente();
            columna[1] = cliente.get(i).getNombre();
            columna[2] = cliente.get(i).getApellidos();
            columna[3] = cliente.get(i).getDomicilio();
            columna[4] = cliente.get(i).getTelefono();
            columna[5] = cliente.get(i).getMail();

            modeloTabla.addRow(columna);
        }
        cone.cerrar();
     }
    

